import React, { Component } from 'react';

import Person from './Person/Person'

class people extends Component {

    state = {
        people: [],
        endpoint: 'ws://localhost:7770'
    }

    componentDidMount(){
        this.socket = new WebSocket(this.state.endpoint);
        this.socket.onopen = () => {
            this.socket.send('init');
        }
        this.socket.onmessage = e => {
            var message = null;
            try {
                message = JSON.parse(e.data);
            } catch(e) {
                console.log('malformed message');
            }
            if (message) {
                const people = [...this.state.people];
                console.log("in the socket:", people[message.id]);
                if (people[message.id]){
                    people.splice(message.id, 0, {id: message.id, name: message.name, number: message.value});
                }else{
                    people.splice(message.id, 1, {id: message.id, name: message.name, number: message.value});
                }
                // people[message.id]= {id: message.id, name: message.name, number: message.value};
                this.setState({people:people});
            }
        } 
    }

    componentWillUnmount(){
        this.socket.close();
    }

    render(){
        console.log("In the render:", this.state.people);
        return this.state.people.filter( el => {return el !== undefined}).map( person => {
            return <Person
                key = {person.id} 
                id = {person.id} 
                name = {person.name} 
                number = {person.number}/>
        })
    }
}

export default people;

