import React, { Component } from 'react';

import Person from './Person/Person'

class people extends Component {

    state = {
        people: [],
        endpoint: 'ws://localhost:7770'
    };

    componentDidMount(){
        this.socket = new WebSocket(this.state.endpoint);
        this.socket.onopen = () => {
            this.socket.send('init');
        }
        this.socket.onmessage = e => {
            var message = null;
            try {
                message = JSON.parse(e.data);
            } catch(e) {
                console.log('malformed message');
            }
            if (message) {
                let data = JSON.parse(e.data);
                const people = [...this.state.people];
                people[data.id]= {id: data.id, name: data.name, number: data.value};
                console.log("ID:", data.id);
                this.setState({people:people});
            }
        } 
    }

    // shouldComponentUpdate(nextProps, nextState){
    //     const nextStatePeopleLength = nextState.people.filter( el => {return el !== undefined}).length;
    //     const currentStatePeopleLength = this.state.people.filter( el => {return el !== undefined}).length;
    //     return nextStatePeopleLength !== currentStatePeopleLength;
    // }

    componentWillUnmount(){
        this.socket.close();
    }

    render(){
        return this.state.people.filter( el => {return el !== undefined}).map( person => {
            return <Person
                key = {person.id} 
                id = {person.id} 
                name = {person.name} 
                number = {person.number}/>
            })
    }
}

export default people;
