import React, { Component } from 'react';

import Person from './Person/Person'

class people extends Component {

    constructor(props){
        super(props);
        this.state = {
            people: [],
            endpoint: 'ws://localhost:7770',
            response: false
        };
    }

    componentDidMount(){
        const socket = new WebSocket(this.state.endpoint);
        socket.onopen = () => {
            socket.send('init');
        }
        socket.onmessage = e => {
            var message = null;
            try {
                message = JSON.parse(e.data);
            } catch(e) {
                console.log('malformed message');
            }
            if (message) {
                let data = JSON.parse(e.data);
                const people = [...this.state.people];
                people[data.id]= {id: data.id, name: data.name, number: data.value};
                this.setState({people:people});
            }
        } 
    }

    componentWillUnmount(){
        socket.close();
    }

    render(){
        console.log(this.state.people);
        return this.state.people.filter( el => {return el !== undefined}).map( (person, index) => {
            return <Person
                key = {index} 
                id = {person.id} 
                name = {person.name} 
                number = {person.number}/>
            })
    }
}

export default people;
