import React, { Component } from 'react';

import Person from './Person/Person'

class people extends Component {

    constructor(props){
        super(props);

        this.state = {
            people: []
        };

        const ws = new WebSocket('ws://localhost:7770');
        ws.onopen = () => {
            ws.send('init');
        }

        this.state.people.push(ws.onmessage = e => {
            var message = null;
            try {
                message = JSON.parse(e.data);
            } catch(e) {
                console.log('malformed message');
            }
            if (message) {
                data = JSON.parse(e.data);
                console.log("id:", data.id)
                // console.log("the record:", {key: e.data.id, id: e.data.id, name: e.data.name, number: e.data.value});
                // return {key: e.data.id, id: e.data.id, name: e.data.name, number: e.data.value};
            }
        });
    }

    render(){
        return this.state.people.map( (person, index) => {
            return <Person
                key = {person.id} 
                id = {person.id} 
                name = {person.name} 
                number = {person.number}/>
        });
    }
}

export default people;
