import React from 'react';
import { Jumbotron, Button } from 'reactstrap';

const topbar = (props) => {
    let buttonTitle = this.statebuttonTitle;
    if (doesShow === false){
        buttonTitle = "Disconnect From Websocket";
    } else {
        buttonTitle = "Connect To Websocket";
    }
    return (
        <Jumbotron>
            <h1 className="display-3">{props.title}</h1>
            <Button color="primary"
                onClick={props.clicked}>{props.buttonTitle}</Button>
        </Jumbotron>
    );
};

export default topbar;
