import React, { Component } from 'react';
import './App.css';
import People from '../components/People/People';
import Topbar from '../components/Topbar/Topbar';

class App extends Component {
    state = {
        showPersons: false,
        buttonTitle: "Connect To Websocket"
    }

    webSocketStreamToggle = () => {
        const doesShow = this.state.showPersons;
        let buttonTitle = this.statebuttonTitle;
        if (doesShow === false){
            buttonTitle = "Connect To Websocket";
        } else {
            buttonTitle = "Disconnect From Websocket";
        }
        this.setState({ showPersons: !doesShow, buttonTitle: buttonTitle});
    }


    render() {
        let people = null
        if (this.state.showPersons) {
            people = <People />
        }

        return (
            <div className="App">
                <Topbar
                    title = "Dominic Scotto Homework"
                    buttonTitle = {this.buttonTitle}
                    clicked = {this.webSocketStreamToggle} />
                {people}
            </div>
        );
    }
}

export default App;
